sourcesContent:
import express from 'express';
import estudiantesRoutes from './src/routes/estudiantes.routes';
import categoriesRoutes from './src/routes/categories.routes';
import cursosRoutes from './src/routes/cursos.routes';
import divisionesRoutes from './src/routes/divisiones.routes';
import authRoutes from './src/routes/auth.routes';
import { createCategories, createDivisiones, createRoles } from './src/libs/initialSetup';

const app = express();

//Ejecutar funci贸n para crear roles
createRoles();
createCategories();
createDivisiones();

//Establecer la ruta inicial
app.get('/', (req, res) => {
    res.send('Bienvenido a mi API');
});

app.use(express.json());
app.use('/estudiantes', estudiantesRoutes);
app.use('/categories', categoriesRoutes);
app.use('/cursos', cursosRoutes);
app.use('/divisiones', divisionesRoutes);
app.use('/api/auth', authRoutes);
export default app;

