sourcesContent:
import Estudiante from "../models/Estudiante";
import Division from "../models/Division";

//Obtener todos los estudiantes
export const getEstudiantes = async (req, res) => {
    const estudiantes = await Estudiante.find();
    res.json(estudiantes);
}

//Crear un estudiante
export const createEstudiante = async (req, res) => {
    try{
        const { name, lname, division, age } = req.body;
        const newEstudiante = new Estudiante({ name, lname, age });
        const foundDivision = await Division.find({ name: { $in: division }});
        newEstudiante.division = foundDivision.map(division => division._id);
        const estudianteSave = await newEstudiante.save();
        res.status(201).json(estudianteSave)
    } catch(error){
        res.status(500).json({ message: error.message })
    }
}

//Obtener un estudiante por su ID
export const getEstudianteById = async (req, res) => {
    const { EstudianteId } = req.params;
    try {
        const estudiante = await Estudiante.findById(EstudianteId);
        if (!estudiante) return res.status(404).json({ message: 'Estudiante no encontrado' });
        res.json(estudiante);
    } catch (error) {
        res.status(500).json({ message: 'Error al obtener el estudiante' });
    }
};

//Actualizar un estudiante por su ID
export const updateEstudianteById = async (req, res) => {
    const { EstudianteId } = req.params;
    const { name, lname, division, age } = req.body;
    try {
        const updatedEstudiante = await Estudiante.findByIdAndUpdate(EstudianteId, { name, lname, division, age }, { new: true });
        if (!updatedEstudiante) return res.status(404).json({ message: 'Estudiante no encontrado' });
        res.json(updatedEstudiante);
    } catch (error) {
        res.status(400).json({ message: 'Error al actualizar el estudiante' });
    }
};

//Eliminar un estudiante por su ID
export const deleteEstudianteById = async (req, res) => {
    const { EstudianteId } = req.params;
    try {
        const deletedEstudiante = await Estudiante.findByIdAndDelete(EstudianteId);
        if (!deletedEstudiante) return res.status(404).json({ message: 'Estudiante no encontrado' });
        res.json({ message: 'Estudiante eliminado' });
    } catch (error) {
        res.status(500).json({ message: 'Error al eliminar el estudiante' });
    }
};

