import { Schema, model } from "mongoose";

// Definir el esquema del estudiante
const estudianteSchema = new Schema({
    name: {
        type: String,
        required: true
    },
    lname: {
        type: String,
        required: true
    },
    division: {
        type: Schema.Types.ObjectId,
        ref: 'Division',
        required: true
    },
    age: {
        type: Number,
        required: true
    }
}, {
    timestamps: true,
    versionKey: false
});

// Exportar el modelo
export default model('Estudiante', estudianteSchema);

