import jwt from 'jsonwebtoken';
import User from '../models/Users';
import Role from '../models/Roles';

//Validar si el token es v谩lido
export const verifyToken = async (req, res, next) => {
    const token = req.headers['x-access-token']
    if (!token) return res.status(403).json({ message: "No se ha proporcionado ning煤n Token" });
    //console.log(token);

    //Extraer la informaci贸n del token
    const decoded = jwt.verify(token, process.env.SECRET);
    req.userId = decoded.id;
    console.log(decoded);

    //Buscar el usuario en la base de datos
    const user = await User.findById(req.userId, { password: 0 });
    console.log(user);

    //Validar si el usuario existe
    if (!user) return res.status(404).json({ message: "Usuario no encontrado" });

    //Continuar con la siguiente funci贸n si el usuario existe
    next();
}

export const isStudent = async (req, res, next) => {
    const user = await User.findById(req.userId).populate("roles");
    const roles = await Role.find({ _id: { $in: user.roles } });

    for (let i = 0; i < roles.length; i++) {
        if (roles[i].name === "student") {
            next();
            return;
        }
    }

    return res.status(403).json({ message: "Require Student Role" });
};

export const isProfessor = async (req, res, next) => {
    //Buscar el usuario en la base de datos
    const user = await User.findById(req.userId);
    //Buscar los roles del usuario
    const roles = await Role.find({ _id: { $in: user.roles } });
    //console.log(roles);
    //Recorrer los roles del usuario
    for (let i = 0; i < roles.length; i++) {
        if (roles[i].name === "professor") {
            next();
            return;
        }
    }
    //Si el usuario no es administrador
    return res.status(403).json({ message: "Requiere ser administrador" });
}

export const isAdmin = async (req, res, next) => {
    //Buscar el usuario en la base de datos
    const user = await User.findById(req.userId);
    //Buscar los roles del usuario
    const roles = await Role.find({ _id: { $in: user.roles } });
    //console.log(roles);
    //Recorrer los roles del usuario
    for (let i = 0; i < roles.length; i++) {
        if (roles[i].name === "admin") {
            next();
            return;
        }
    }
    //Si el usuario no es administrador
    return res.status(403).json({ message: "Requiere ser administrador" });
}

export const isAdminOrProfessor = async (req, res, next) => {
    //Buscar el usuario en la base de datos
    const user = await User.findById(req.userId);
    //Buscar los roles del usuario
    const roles = await Role.find({ _id: { $in: user.roles } });
    //console.log(roles);
    //Recorrer los roles del usuario
    for (let i = 0; i < roles.length; i++) {
        if (roles[i].name === "admin" || roles[i].name === "professor") {
            next();
            return;
        }
    }
    //Si el usuario no es administrador
    return res.status(403).json({ message: "Requiere ser administrador o profesor" });
}

