sourcesContent:
import Category from "../models/Category";
import Curso from "../models/Curso";

//Obtener todas las categorias
export const getCategories = async (req, res) => {
    try{
        const categories = await Category.find();
    res.json(categories);
    } catch(error){
        res.status(500).json({message: error.message})
    }
}

//Crear una categoria
export const createCategory = async (req, res) => {

    try {
        const { name } = req.body;
        const newCategory = new Category({ name });
        const categorySave = await newCategory.save();
        res.status(201).json(categorySave);
    } catch (error) {
        res.status(500).json({ message: error.message });
    }
}

//Obtener una categoria por su ID
export const getCategoryById = async (req, res) => {
    const { CategoryId } = req.params;
    try {
        const category = await Category.findById(CategoryId);
        if (!category) return res.status(404).json({ message: 'Categoria no encontrado' });
        res.json(category);
    } catch (error) {
        res.status(500).json({ message: 'Error al obtener la categoria' });
    }
};

//Actualizar una categoria por su ID
export const updateCategoryById = async (req, res) => {
    const { CategoryId } = req.params;
    const { name } = req.body;
    try {
        const updatedCategory = await Category.findByIdAndUpdate(CategoryId, { name }, { new: true });
        if (!updatedCategory) return res.status(404).json({ message: 'Categoria no encontrada' });
        res.json(updatedCategory);
    } catch (error) {
        res.status(400).json({ message: 'Error al actualizar la categoria' });
    }
};

//Eliminar una categoria por su ID
export const deleteCategoryById = async (req, res) => {
    const { CategoryId } = req.params;
    try {
        await Curso.updateMany(
            { category: CategoryId },
            { $set: { category: "6691e586dc94103aeca462e8" } }  
        );

        const deletedCategory = await Category.findByIdAndDelete(CategoryId);
        if (!deletedCategory) return res.status(404).json({ message: 'Categoria no encontrada' });
        res.json({ message: 'Categoria eliminada' });
    } catch (error) {
        res.status(500).json({ message: 'Error al eliminar la categoria' });
    }
};

