sourcesContent:
import { Schema, model } from "mongoose";

// Definir el esquema del curso
const cursoSchema = new Schema({
    name: {
        type: String,
        required: true
    },
    category: {
        type: Schema.Types.ObjectId,
        ref: 'Category',
        required: true
    }
}, {
    timestamps: true,
    versionKey: false
});

// Exportar el modelo
export default model('Curso', cursoSchema);

