import { Router } from "express";
import * as cursosCtrl from '../controllers/cursos.controller';
import { authJwt } from '../middlewares';

const router = Router();

//Establecer ruta cursos mediante GET
router.get('/', cursosCtrl.getCursos);
router.post('/', [authJwt.verifyToken, authJwt.isAdmin], cursosCtrl.createCurso);
router.get('/:CursoId', cursosCtrl.getCursoById);
router.put('/:CursoId', [authJwt.verifyToken, authJwt.isAdminOrModerator], cursosCtrl.updateCursoById);
router.delete('/:CursoId', [authJwt.verifyToken, authJwt.isAdminOrModerator], cursosCtrl.deleteCursoById);

export default router;

