sourcesContent:
import Curso from "../models/Curso";
import Category from "../models/Category";

//Obtener todos los cursos
export const getCursos = async (req, res) => {
    const cursos = await Curso.find().populate('category', 'name');;
    res.json(cursos);
}

//Crear un curso
export const createCurso = async (req, res) => {
    try{
        const { name, category } = req.body;
        const newCurso = new Curso({ name });
        const foundCategory = await Category.find({ name: { $in: category }});
        newCurso.category = foundCategory.map(category => category._id);
        const cursoSave = await newCurso.save();
        res.status(201).json(cursoSave)
    } catch(error){
        res.status(500).json({ message: error.message })
    }
}

//Obtener un curso por su ID
export const getCursoById = async (req, res) => {
    const { CursoId } = req.params;
    try {
        const curso = await Curso.findById(CursoId).populate('category', 'name');;
        if (!curso) return res.status(404).json({ message: 'Curso no encontrado' });
        res.json(curso);
    } catch (error) {
        res.status(500).json({ message: 'Error al obtener el curso' });
    }
};

// Actualizar un curso por su ID
export const updateCursoById = async (req, res) => {
    const { CursoId } = req.params;
    const { name, category } = req.body;
    try {
        // Buscar las categor铆as correspondientes
        const foundCategory = await Category.find({ name: { $in: category }});
        
        // Extraer los IDs de las categor铆as encontradas
        const categoryIds = foundCategory.map(category => category._id);
        
        // Actualizar el curso con los nuevos datos y las categor铆as
        const updatedCurso = await Curso.findByIdAndUpdate(
            CursoId,
            { name, category: categoryIds },
            { new: true }
        );

        if (!updatedCurso) return res.status(404).json({ message: 'Curso no encontrado' });
        
        res.json(updatedCurso);
    } catch (error) {
        res.status(400).json({ message: 'Error al actualizar el curso', error: error.message });
    }
};


//Eliminar un curso por su ID
export const deleteCursoById = async (req, res) => {
    const { CursoId } = req.params;
    try {
        const deletedCurso = await Curso.findByIdAndDelete(CursoId);
        if (!deletedCurso) return res.status(404).json({ message: 'Curso no encontrado' });
        res.json({ message: 'Curso eliminado' });
    } catch (error) {
        res.status(500).json({ message: 'Error al eliminar el curso' });
    }
};

