sourcesContent:
import { Router } from "express";
import * as divisionesCtrl from '../controllers/divisiones.controller';
import { authJwt } from '../middlewares';

const router = Router();

//Establecer ruta divisiones mediante GET
router.get('/', divisionesCtrl.getDivisiones);
router.post('/', [authJwt.verifyToken, authJwt.isAdmin], divisionesCtrl.createDivision);
router.get('/:DivisionId', divisionesCtrl.getDivisionById);
router.put('/:DivisionId', [authJwt.verifyToken, authJwt.isAdminOrModerator], divisionesCtrl.updateDivisionById);
router.delete('/:DivisionId', [authJwt.verifyToken, authJwt.isAdminOrModerator], divisionesCtrl.deleteDivisionById);

export default router;

