sourcesContent:
import { Router } from "express";
import * as estudiantesCtrl from '../controllers/estudiantes.controller';
import { authJwt } from '../middlewares';

const router = Router();

//Establecer ruta estudiantes mediante GET
router.get('/', estudiantesCtrl.getEstudiantes);
router.post('/', [authJwt.verifyToken, authJwt.isAdmin], estudiantesCtrl.createEstudiante);
router.get('/:EstudianteId', estudiantesCtrl.getEstudianteById);
router.put('/:EstudianteId', [authJwt.verifyToken, authJwt.isAdminOrModerator], estudiantesCtrl.updateEstudianteById);
router.delete('/:EstudianteId', [authJwt.verifyToken, authJwt.isAdminOrModerator], estudiantesCtrl.deleteEstudianteById);

export default router;

