sourcesContent:
import { Router } from "express";
import * as categoriesCtrl from '../controllers/categories.controller';
import { authJwt } from '../middlewares';

const router = Router();

//Establecer ruta categorias mediante GET
router.get('/', categoriesCtrl.getCategories);
router.post('/', [authJwt.verifyToken, authJwt.isAdmin], categoriesCtrl.createCategory);
router.get('/:CategoryId', categoriesCtrl.getCategoryById);
router.put('/:CategoryId', [authJwt.verifyToken, authJwt.isAdminOrModerator], categoriesCtrl.updateCategoryById);
router.delete('/:CategoryId', [authJwt.verifyToken, authJwt.isAdminOrModerator], categoriesCtrl.deleteCategoryById);

export default router;

